export const getAllprofile = async (req, res) => {
  try {
    const Profile = await ProfileModel.find();
    return res.status(200).json({
      status: "success",
      number: Profile.length,
      Profile,
    });
  } catch (error) {
    return res.status(404).json({
      status: "failed",
      error: error.message,
    });
  }
};

